
public class MemberVariableMain {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i;
		i=34;
		//FirstClass(참조변수,클래스변수)변수선언 
		MemberVariable fc;  //4byte 기억장소 할당
		
		//객체를 찍어다(인스턴스생성) : 멤버변수기억장소할당
		fc = new MemberVariable();//초기화
		
		//객체의 멤버변수에 데이타를 입력
		fc.member1=45;
		fc.member2="문자열";
		fc.member3=true;
		
		System.out.println(fc.member1);
		System.out.println(fc.member2);
		System.out.println(fc.member3);
		
	}

}
