package variabledeclare;

public class VariableTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// 1. 문자형
		char munja1, munja2, munja3, munja4;
		munja1 = 'A';
		munja2 = 65; // 타입이랑 식별자 크기가 다름
		munja3 = '김';
		munja4 = 44608; // 타입이랑 식별자 크기가 다름

		System.out.println(munja1);
		System.out.println(munja2);
		System.out.println(munja3);
		System.out.println(munja4);

		// 2. 숫자형
	
		// 2-1. 정수형
		byte b;
		short s;
		int i;
		long l;

		b = 127; //식별자 4바이트 타입 1바이트 뭥미??
		s = 32767; // 식별자 4바이트 타입 2바이트 뭥미??
		i = 2147483647;

		l = 2147483648L;
		
		// 2-2. 실수형
		float f;
		double d;

		f = 3.123456789f;
		d = 3.12345678;

		System.out.println(f);
		System.out.println(d);

		// 3. 논리형
		boolean b1;
		boolean b2;

		b1 = true;
		b2 = false;

		System.out.println(b1);
		System.out.println(b2);
	}

}
