			System.out.println("1.--------------------");
			boolean isAlive = true;
			int i = 0;
			while(isAlive){
				System.out.println("i변수의 값:"+i);
				i++;
				if(i==10)
					isAlive=false;
				
			}
			System.out.println("2.--------------------");
			
			isAlive = true;
			int j = 0;
			
			while(isAlive){
				System.out.println("j변수의 값"+j);
				j++;
				if(j==10)
					break;
			}
			
			System.out.println("3.--------------------");
			
			int k = 0;
			while(k<10){
				System.out.println("A.K변수값" + k);
			}
				
			
		
